WITH architectures AS (
  SELECT DISTINCT ON (peer) peer, value "arch"
  FROM properties  where name = 'process.runtime.arch'
)
SELECT arch "Architecture", (COUNT(peer) / (SELECT COUNT(arch) FROM architectures)) "Total"
FROM architectures GROUP BY arch;

WITH oss AS (
  SELECT DISTINCT ON (peer) peer, value "os"
  FROM properties  where name = 'process.runtime.os'
)
SELECT os "OS", (COUNT(peer) / (SELECT COUNT(os) FROM oss)) "Total"
FROM oss GROUP BY os;
